    Subroutine ConstructRelaxation

        !Arguments-------------------------------------------------------------



        !Local-----------------------------------------------------------------

        integer                          :: ILB, IUB, JLB, JUB, KLB, KUB
        integer                          :: ILBWork, IUBWork, JLBWork, JUBWork, KLBWork, KUBWork
        integer                          :: STATUS

        integer                          :: iflag,fromfile


        IUB = Me%Size%IUB
        ILB = Me%Size%ILB
        JUB = Me%Size%JUB
        JLB = Me%Size%JLB
        KUB = Me%Size%KUB
