    !----------------------------------------------------------------------------

    function F_Courant(Velocity, DT, DX)
    real ::  F_Courant

        !Arguments---------------------------------------------------------------

        real, intent(IN) :: Velocity, DT, DX

        !------------------------------------------------------------------------

        F_Courant = Velocity * DT / DX

        !------------------------------------------------------------------------

    end function F_Courant

