    subroutine OutputCourant(Courant, I, J)

        !Arguments---------------------------------------------------------------

        integer, intent(IN) :: I, J
        real,    intent(IN) :: Courant

        !------------------------------------------------------------------------

#ifndef _OUTPUT_OFF_
        write(*, *)"---------------------- HYDRODYNAMICS ---------------------"
        write(*, *)
        write(*, *)"Courant Number is : ", Courant
        write(*, *)"In Grid Cell[i,j] : ", i, j
        write(*, *)
#endif

        !------------------------------------------------------------------------

    end subroutine OutputCourant

