cd7:                        if ((Faces3D_VSon        (i, j, k) == Covered   .or.    &
                             ImposedTangFacesVSon(i, j, k) == Imposed   .or.        &
                             ImposedNormFacesVSon(i, j, k) == Imposed)  .and.       &
