                                 (1 - LandBoundaryFacesU(i  , j+1, k)) * Velocity_U_New(i  , j+1, k)
                        NoLand1  = 2 - Flag1
                    endif

                    Flag2 = LandBoundaryFacesU(i-1, j, k) + LandBoundaryFacesU(i, j, k)

                    if (Flag2 == 0) then
                        VUvar2 = (Velocity_U_New(i-1, j, k) * DYY(i, j)   + &
                                  Velocity_U_New(i,   j, K) * DYY(i-1, j))/ (DYY(i-1, j) +  DYY(i, j))
