                    do k = Kbottom, KUB
                        ![s/m]                   =   [s] /  [m]
                        DT_Z                     =   DT_Velocity / DUZ(i, j, k)
                        BottomFaceViscosity      =   TopFaceViscosity

                        !Horizontal viscosity faces interpolation
