                                     Array3D        = Aux3DR8,                              &
                                     STAT           = STAT_CALL)
                if (STAT_CALL /= SUCCESS_) stop 'Read_Final_HDF5 - ModuleHydrodynamic - ERR340'

                Me%Residual%WaterFlux_X(ILB:IUB,JLB:JUB,KLB:KUB) = Aux3DR8(ILW:IUW,JLW:JUW,KLB:KUB)

                !read(InitialFile) (((Me%Residual%WaterFlux_Y(i, j, k) ,         &
