            else if (.not. Residual .and. Me%ComputeOptions%Residual) then cd1

                    Me%Residual%ResidualTime    =  0


                    do  j = JLB, JUB
                    do  i = ILB, IUB

                        Me%Residual%WaterLevel(i, j)    =  0.

                        do  k = KLB, KUB

