#endif
                            ErrorOccured = .true.
                            if (Me%SubModel%MissingNull)                            &
                                NullValue = .true.

                        endif

                        if      (ImposedTangFacesVSon(i, j, k) == Imposed .or.      &
                                 Faces3D_VSon        (i, j, k) == Covered) then

                            i_thick = i
                            j_thick = j

                        else if (ImposedNormFacesVSon(i, j, k) == Imposed) then

