            !$OMP END PARALLEL
            
            if (Me%SubModel%MissingNull) then

                 call RemoveLowerSpikes (Me%SubModel%Z, Water3DSon,                 &
                                        Bathymetry, MinWaterColumn,                 &
                                        Me%SubModel%DeadZonePoint,                  &
                                        Me%SubModel%DeadZone,                       &
                                        ILB, IUB, JLB, JUB, KUB) 
